CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime);
INSERT INTO users VALUES(1,'Admin','admin@alnafi.com',NULL,'$2y$12$5u/cuE1TAbJbvJRL0vuQt.vFg09dBR5rsU0t2S4d8pcJNzAHk20/K',NULL,'2025-11-05 07:09:03','2025-11-05 07:09:03');
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer primary key autoincrement not null, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" varchar not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "customers" ("id" integer primary key autoincrement not null, "name" varchar not null, "phone" varchar, "email" varchar, "address" text, "status" varchar check ("status" in ('active', 'inactive')) not null default 'active', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "suppliers" ("id" integer primary key autoincrement not null, "name" varchar not null, "phone" varchar, "email" varchar, "address" text, "status" varchar check ("status" in ('active', 'inactive')) not null default 'active', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "items" ("id" integer primary key autoincrement not null, "name" varchar not null, "category" varchar, "unit" varchar not null default 'pcs', "description" text, "status" varchar check ("status" in ('active', 'inactive')) not null default 'active', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "purchases" ("id" integer primary key autoincrement not null, "supplier_id" integer not null, "item_id" integer not null, "quantity" numeric not null, "unit_price" numeric not null, "total_amount" numeric not null, "purchase_date" date not null, "reference_no" varchar, "notes" text, "created_at" datetime, "updated_at" datetime, foreign key("supplier_id") references "suppliers"("id") on delete cascade, foreign key("item_id") references "items"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "customer_payments" ("id" integer primary key autoincrement not null, "customer_id" integer not null, "amount" numeric not null, "payment_date" date not null, "payment_method" varchar not null default 'cash', "reference_no" varchar, "notes" text, "created_at" datetime, "updated_at" datetime, foreign key("customer_id") references "customers"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "incomes" ("id" integer primary key autoincrement not null, "customer_id" integer not null, "item_id" integer, "amount" numeric not null, "income_date" date not null, "reference_no" varchar, "description" text, "status" varchar check ("status" in ('pending', 'completed', 'cancelled')) not null default 'completed', "created_at" datetime, "updated_at" datetime, foreign key("customer_id") references "customers"("id") on delete cascade, foreign key("item_id") references "items"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "supplier_payments" ("id" integer primary key autoincrement not null, "supplier_id" integer not null, "amount" numeric not null, "payment_date" date not null, "payment_method" varchar not null default 'cash', "reference_no" varchar, "notes" text, "created_at" datetime, "updated_at" datetime, foreign key("supplier_id") references "suppliers"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "expenses" ("id" integer primary key autoincrement not null, "category" varchar not null, "amount" numeric not null, "expense_date" date not null, "reference_no" varchar, "description" text, "status" varchar check ("status" in ('pending', 'paid', 'cancelled')) not null default 'paid', "created_at" datetime, "updated_at" datetime);
